import React ,{Fragment, useState} from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

//Action :_xxYYYY
import { _setAlert } from "../../actions/alert";



const Register = (props) => {

    //Definition du state : Hook[state, setState ] =  useSate(initialize)
    const [formData, setFormData] = useState({
        name:'',
        email: '',
        password: '',
        password2: ''
    });


  // const { name, email, password, password2 } = this.state;
    const { name, email, password, password2 } = formData;



    const onChange = (e)=>{

        setFormData({ ...formData, [e.target.name]: e.target.value })
        //setState({ x : newValue})
    };


    const onSubmit = async(e)=>{

        e.preventDefault();
        /**
         * Check matching password
         */
        if(password !== password2){
            props._setAlert('password no correspondance', 'danger');

            console.log('Register:', 'password no correspondance');

        }else{

            console.log('SUCCESS',formData);
        // /**
        //  * Create user Object
        //  */
        //     const newUser ={
        //         name,
        //         email,
        //         password
        //     }

        // /**
        //  * Async/await
        //  * Define headers like postman
        //  * Convert object javascript to object json (stringify)
        //  * Launch request axios.
        //  */
        //     try{
        //          const config= {
        //             headers: {
        //                 'Content-Type':'application/json'
        //             }
        //         }

        //         const body  =  JSON.stringify(newUser);

        //         let res =  await axios.post('/api/users', body, config);

        //         console.log(res.data)


        //     } catch (error) {
        //         console.error(error.response.data)
        //     }
        }
    }


    return (
        <Fragment>
            <h1 className="large text-primary">Sign Up</h1>
            <p className="lead"><i className="fas fa-user"></i> Create Your Account</p>

            <form className="form" onSubmit = { e=> onSubmit(e) } >
                <div className="form-group">
                    <input 
                        type="text" 
                        placeholder="Name"
                        name="name"
                        value={name}
                        onChange={e => onChange(e)}
                        required />
                </div>
                <div className="form-group">
                    <input 
                        type="email" 
                         placeholder="Email Address" 
                         name="email"
                         value={email} 
                         onChange= {e=> onChange(e)}
                         required
                         />
                    <small className="form-text">This site uses Gravatar so if you want a profile image, use a Gravatar email</small>
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={e => onChange(e)}
                        minLength="6"
                    />
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        name="password2"
                        value={password2}
                        onChange={e => onChange(e)}
                        minLength="6"
                    />
                </div>
                <input type="submit" className="btn btn-primary" value="Register" />
            </form>

            <p className="my-1">
                Already have an account? <Link to="/login">Sign In</Link>
            </p>
        </Fragment>
    );
};

 Register.propTypes = {
    _setAlert : PropTypes.func.isRequired,
}

export default connect(null, {_setAlert}) (Register)
